using DealerOn.Web.Models;
using Microsoft.AspNetCore.Builder;
using Microsoft.AspNetCore.Hosting;
using Microsoft.AspNetCore.HttpsPolicy;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace DealerOn
{
    public class Startup
    {
        public Startup(IConfiguration configuration)
        {
            Configuration = configuration;
        }

        public IConfiguration Configuration { get; }

        // This method gets called by the runtime. Use this method to add services to the container.
        public void ConfigureServices(IServiceCollection services)
        {
            services.AddControllersWithViews();
            services.AddDbContext<DealerOnContext>(options => options.UseSqlite("Data Source=DealerOn.db"));                      
        }

        // This method gets called by the runtime. Use this method to configure the HTTP request pipeline.
        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
        {
            SetupScripts(app);
            if (env.IsDevelopment())
            {
                app.UseDeveloperExceptionPage();
            }
            else
            {
                app.UseExceptionHandler("/Home/Error");
                // The default HSTS value is 30 days. You may want to change this for production scenarios, see https://aka.ms/aspnetcore-hsts.
                app.UseHsts();
            }
            app.UseHttpsRedirection();
            app.UseStaticFiles();

            app.UseRouting();

            app.UseAuthorization();

            app.UseEndpoints(endpoints =>
            {
                endpoints.MapControllerRoute(
                    name: "default",
                    pattern: "{controller=Products}/{action=Index}/{id?}");
            });
        }

        async void SetupScripts(IApplicationBuilder app)
        {
            // Setup the Tables
            using var serviceScope = app.ApplicationServices.GetService<IServiceScopeFactory>().CreateScope();
            var context = serviceScope.ServiceProvider.GetRequiredService<DealerOnContext>();
            var canConnect = await context.Database.CanConnectAsync();
            if (canConnect)
            {

                if (context.Categories.ToList().Count() == 0)
                {
                    Category[] categories = new Category[]
                    {
                        new Category() { Name = Category.Categories.Books, SalesTax = false   },
                        new Category() { Name = Category.Categories.Food, SalesTax = false  },
                        new Category() { Name = Category.Categories.Medical, SalesTax = false },
                        new Category() { Name = Category.Categories.Other, SalesTax = true }
                    };

                    context.Categories.AddRange(categories);
                    context.SaveChanges();
                }

                if (context.Products.Count() == 0)
                {

                    var bookId = context.Categories.Single(cat => cat.Name == Category.Categories.Books).CategoryId;
                    var foodId = context.Categories.Single(cat => cat.Name == Category.Categories.Food).CategoryId;
                    var medicalId = context.Categories.Single(cat => cat.Name == Category.Categories.Medical).CategoryId;
                    var otherId = context.Categories.Single(cat => cat.Name == Category.Categories.Other).CategoryId;


                    Product[] products = new Product[]
                        {
                           new Product() { Name = "Book", Price = 12.49m, CategoryId = bookId, Image = "data:image/png;base64,"},
                           new Product() { Name = "Music CD", Price = 14.99m, CategoryId = otherId, Image = "data:image/jpeg;base64,"},
                           new Product() { Name = "Chocalate bar", Price = 0.85m, CategoryId = foodId, Image = "data:image/jpeg;base64,"},
                           new Product() { Name = "Imported box of chocalates", Price = 10.00m, CategoryId = foodId, Imported = true, Image = "data:image/jpeg;base64," },
                           new Product() { Name = "Imported bottle of perfume", Price = 47.50m, CategoryId = otherId, Imported = true, Image = "data:image/jpeg;base64,"},
                        };

                    context.Products.AddRange(products);

                    context.SaveChanges();
                }


            }
        }
    }
}

